import React from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../client';
import './EditPost.css'


const EditPost = ({data}) => {

    const {id} = useParams();
    //const post = data.filter(item => item.id === id)[0];
    const [updatedPost, setUpdatedPost] = useState({
        title: post.title,
        author: post.author,
        description: post.description,
    });

    const updatePost = async (event) => {
        event.preventDefault();

        await supabase
        .from('Posts')
        .update({ title: updatedPost.title, author: updatedPost.author,  description: updatedPost.description})
        .eq('id', id);

        // Redirect to the home page or do whatever you want to do after creating the post
        window.location = "/";
    }

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setUpdatedPost({
            ...updatedPost,
            [name]: value,
        });
    };

    return (
        <div>
            <form onSubmit={updatePost}>
                <label for="title">Title</label> <br />
                <input type="text" id="title" name="title" value={post.title} onChange={handleInputChange}/><br />
                <br/>

                <label for="author">Author</label><br />
                <input type="text" id="author" name="author" value={post.author} onChange={handleInputChange}/><br />
                <br/>

                <label for="description">Description</label><br />
                <textarea rows="5" cols="50" id="description" value={post.description} onChange={handleInputChange}>
                </textarea>
                <br/>
                <input type="submit" value="Submit" />
                <button className="deleteButton">Delete</button>
            </form>
        </div>
    )
}

export default EditPost
