import React, { useState } from 'react';
import { supabase } from '../client';
import './CreatePost.css'

const CreatePost = () => {
    // Define state for post data
    const [post, setPost] = useState({
        title: '',
        author: '',
        description: ''
    });

    const createPost = async (event) => {
        event.preventDefault();

        await supabase
          .from('Posts')
          .insert({ title: post.title, author: post.author, description: post.description })
          .select();

        // Reset the form fields
        setPost({
            title: '',
            author: '',
            description: ''
        });

        // Redirect to the home page or do whatever you want to do after creating the post
        window.location = "/";
    }

    // Update the state when the user types in the form fields
    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setPost({ ...post, [name]: value });
    }

    return (
        <div>
            <form onSubmit={createPost}>
                <label htmlFor="title">Title</label> <br />
                <input type="text" id="title" name="title" value={post.title} onChange={handleInputChange} /><br />
                <br/>

                <label htmlFor="author">Author</label><br />
                <input type="text" id="author" name="author" value={post.author} onChange={handleInputChange} /><br />
                <br/>

                <label htmlFor="description">Description</label><br />
                <textarea rows="5" cols="50" id="description" name="description" value={post.description} onChange={handleInputChange}>
                </textarea>
                <br/>
                <input type="submit" value="Submit" />
            </form>
        </div>
    )
}

export default CreatePost
